-- name: get-all
-- record_class: LogEntry
SELECT
    started_on,
    stopped_on,
    program,
    device_id,
    durability_cost,
    state,
    data
FROM
    log
ORDER BY
    id;

-- name: add<!
INSERT INTO
    log (
        started_on,
        stopped_on,
        program,
        device_id,
        durability_cost,
        state,
        data
    )
VALUES
    (
        :started_on,
        :stopped_on,
        :program,
        :device_id,
        :durability_cost,
        :state,
        :data
    );

-- name: delete!
DELETE FROM
    log