import logging


_LOGGER = logging.getLogger(__name__)


def try_install_uvloop():
	try:
		import uvloop
	except ImportError as error:
		_LOGGER.info("Using asyncio's built-in event loop.")
		_LOGGER.debug('uvloop could not be used: ', exc_info=error)
	else:
		_LOGGER.info("Using uvloop's event loop.")
		uvloop.install()
