"""Sleep functions not found in the standard library (yet)."""
import asyncio
from time import time


async def sleep_until(utc_time_in_seconds):
	"""Sleep until the specified point in time."""
	now = time()
	sleep_time = utc_time_in_seconds - now
	await asyncio.sleep(sleep_time)


async def sleep_forever():
	"""Sleep indefinitely. This coroutine runs forever (or until cancelled)."""
	while True:
		await asyncio.sleep(60 * 60)  # An hour
