from enum import Enum, auto


class TaskState(Enum):
	# Please ensure that the order doesn't change, as this messes up
	# the IDs given to the states. Don't insert new entries in front
	# of the existing ones.
	IDLE = auto()
	RUNNING = auto()
	COMPLETED = auto()
	FAILED = auto()
	CANCELLED = auto()
	# Instead, add new entries here at the end.

	@staticmethod
	def is_done(state) -> bool:
		"""Is the given state either completed, failed, or cancelled."""
		return state in (TaskState.COMPLETED, TaskState.FAILED, TaskState.CANCELLED)
