-- name: get^
SELECT
    value
FROM
    variable
WHERE
    name = :name;

-- name: get-all
SELECT
    *
FROM
    variable;

-- name: upsert-bulk*!
INSERT INTO
    variable (last_modified, name, value)
VALUES
    (:last_modified, :name, :value)
ON CONFLICT(name) DO
                UPDATE
                    SET last_modified=:last_modified,
                        value=:value;